elation.extend("utils.math.gcd", function(t1, t2) {
  var gcd = 1;
  if (t1 > t2) {
    t1 = t1 + t2;
    t2 = t1 - t2;
    t1 = t1 - t2;
  }
  if ((t2 == (Math.round(t2 / t1)) * t1)) {
    gcd = t1
  } else {
    for (var i = Math.round(t1 / 2); i > 1; i--) {
      if ((t1 == (Math.round(t1 / i)) * i) && (t2 == (Math.round(t2 / i)) * i)) {
        gcd = i;
        i=-1;
      }
    }
  }
  return gcd;  
});
